<!-- 策略下單盒 - 策略選擇器 -->
<template>
    <div ref="ctn" class="strategy-selector-ctn flex-col sys-bgc font-size-small mg3 pdt3 pdb5">
		<!-- 收合的圖示 -->
		<div class="flex-end mg1">
			<i class="material-icons" @click="onBtnClose">close</i>
		</div>
		<div class="strategy-btn-ctn list-block-left pdl1">
			<div class="strategy-btn flex-col tcef" v-for="obj in strategyList" :key="obj.key" 
				@click="onBtnStrategy(obj)" :class="{selected: obj.key === selectedStrategy.key}">
				<span :class="[`icon icon-${obj.key}`]"></span>
				<span>{{$ln(obj.label)}}</span>
			</div>
			<!-- 策略說明 -->
			<span class="btn-help mgl5 flex-center tcef" @click="onBtnHelp"><i class="material-icons mgr1">help</i>{{$ln('策略说明')}}</span>
		</div>
		<div class="flex-1" />
    </div>
</template>

<script>
import PlotOrderStrategyHelp from '@/components/PlotOrder/PlotOrderStrategyHelp';
export default {
	props: ["defaultStrategyName"],
	data() {
		let list = [
			{key: "Jump", label: "大涨", BS1: 'B', CP1: 'C', SP1: 'H', colors:[0, 1]},
			{key: "Sink", label: "大跌", BS1: 'B', CP1: 'P', SP1: 'L', colors:[1, 0]},
			{key: "NotRise", label: "不涨", BS1: 'S', CP1: 'C', SP1: 'L', colors:[1, 0]},
			{key: "NotFall", label: "不跌", BS1: 'S', CP1: 'P', SP1: 'H', colors:[0, 1]},
			{key: "BullCallSpread", label: "盘涨Call", BS1: 'B', CP1: 'C', BS2: 'S', CP2: 'C', SP1: 'L', SP2: 'H', colors:[0, 1]},
			{key: "BearCallSpread", label: "盘跌Call", BS1: 'S', CP1: 'C', BS2: 'B', CP2: 'C', SP1: 'L', SP2: 'H', colors:[1, 0]},
			{key: "BullPutSpread", label: "盘涨Put", BS1: 'B', CP1: 'P', BS2: 'S', CP2: 'P', SP1: 'L', SP2: 'H', colors:[0, 1]},
			{key: "BearPutSpread", label: "盘跌Put", BS1: 'S', CP1: 'P', BS2: 'B', CP2: 'P', SP1: 'L', SP2: 'H', colors:[1, 0]},
			{key: "BuyStraddles", label: "原点突破", BS1: 'B', CP1: 'C', BS2: 'B', CP2: 'P', SP1: 'S', SP2: 'S', colors:[1, 0, 1]},
			{key: "SellStraddles", label: "原点盘整", BS1: 'S', CP1: 'C', BS2: 'S', CP2: 'P', SP1: 'S', SP2: 'S', colors:[0, 1, 0]},
			{key: "BuyStrangles", label: "区间突破", BS1: 'B', CP1: 'C', BS2: 'B', CP2: 'P', SP1: 'H', SP2: 'L', colors:[1, 0, 1]},
			{key: "SellStrangles", label: "区间盘整", BS1: 'S', CP1: 'C', BS2: 'S', CP2: 'P', SP1: 'H', SP2: 'L', colors:[0, 1, 0]},
			{key: "Conversions", label: "转换", BS1: 'B', CP1: 'P', BS2: 'S', CP2: 'C', SP1: 'S', SP2: 'S', colors:[1, 0]},
			{key: "Reversals", label: "逆转", BS1: 'S', CP1: 'P', BS2: 'B', CP2: 'C', SP1: 'S', SP2: 'S', colors:[0, 1]},
		];
		return {
			selectedStrategy: {},
			strategyList: list,
		}
	},
	beforeMount() {
		// 預設顯示第一個策略
		this.selectedStrategy = this.strategyList[0];
		// 有帶入預設要顯示的策略時
		if (this.defaultStrategyName) {
			this.selectedStrategy = this.strategyList.find((obj)=>{return obj.key===this.defaultStrategyName});
		}
		this.$emit("change", this.selectedStrategy);
	},
    mounted() {},
	beforeDestroy() {},
	components: {},
    methods: {
		onBtnClose() {
			this.$emit("close");
			this.$refs.ctn.style.top = `100%`;
		},
		onBtnStrategy(obj) {
			if (obj === this.selectedStrategy) {
				this.onBtnClose();
			}
			else {
				this.selectedStrategy = obj;
				this.$emit("change", obj);
			}
		},
		onBtnHelp() {
			eventBus.$emit("POPUP-DIALOG", PlotOrderStrategyHelp, '', {transName: 'float'});
		}
	},
	watch: {},
    computed: {},
}
</script>

