<!-- 策略下單盒 - 策略選擇器 -->
<template>
    <div ref="ctn" class="strategy-selector-ctn flex-col sys-bgc font-size-small mg3 pdt3 pdb5">
		<!-- 收合的圖示 -->
		<div class="flex-end mg1">
			<i class="material-icons" @click="onBtnClose">close</i>
		</div>
		<div class="flex-col flex-1 posr">
			<ScrollBounce  v-stop-propagation-y>
				<div class="strategy-btn-ctn list-block-left pdl1">
					<div class="strategy-btn flex-col tcef" v-for="obj in defaultStrategyList" :key="obj.key"
						@click="onBtnStrategy(obj)" :class="{'selected': obj.key === selectedStrategy.key, 'disabled': obj.hidden, 'opacity0': obj.hidden}">
						<span :class="[`icon icon-${obj.key}`]"></span>
						<span>{{$ln(obj.label)}}</span>
					</div>
					<!-- 策略說明 -->
					<span class="btn-help mgl5 flex-center tcef" @click="onBtnHelp"><i class="material-icons mgr1">help</i>{{$ln('策略说明')}}</span>
				</div>
			</ScrollBounce>
		</div>
    </div>
</template>

<script>
import PlotOrderStrategyHelpTW from '@/components/PlotOrderTW/PlotOrderStrategyHelpTW';
export default {
	props: ["defaultStrategyName"],
	data() {
		// lockCP : CallPut必須相同
		// lockSP : 履約價必須相同
		// lockMTH : 履約月份必須相同
		// oppositeBS : 買賣必須相反
		// sp2MoreThenSP1 : 腳2的履約價必須大於腳1
		// sp2LessThenSP1 : 腳2的履約價必須小於腳1
		let list = [
			{key: "UserDefine", label: "自訂策略",			BS1: 'B', BS2: 'B', CP1: 'C', CP2: 'C', SP1: 'H', SP2: 'H', colors:[0, 1]},
			{key: "StDiffTime", label: "時間價差",			BS1: 'B', BS2: 'B', CP1: 'C', CP2: 'C', SP1: 'H', SP2: 'H', colors:[0, 1], oppositeBS: true, lockCP: true, lockSP: true},
			{key: "StEmpty1", label: "空白", hidden: true},
			{key: "StEmpty2", label: "空白", hidden: true},
			{key: "BullCallSpread", label: "多頭價差(C)",	BS1: 'B', BS2: 'S', CP1: 'C', CP2: 'C', SP1: 'L', SP2: 'H', colors:[0, 1], lockMTH: true, sp2MoreThenSP1: true},
			{key: "BullPutSpread", label: "多頭價差(P)",	BS1: 'B', BS2: 'S', CP1: 'P', CP2: 'P', SP1: 'L', SP2: 'H', colors:[0, 1], lockMTH: true, sp2MoreThenSP1: true},
			{key: "BearCallSpread", label: "空頭價差(C)",	BS1: 'B', BS2: 'S', CP1: 'C', CP2: 'C', SP1: 'L', SP2: 'H', colors:[1, 0], lockMTH: true, sp2LessThenSP1: true},
			{key: "BearPutSpread", label: "空頭價差(P)",	BS1: 'B', BS2: 'S', CP1: 'P', CP2: 'P', SP1: 'L', SP2: 'H', colors:[1, 0], lockMTH: true, sp2LessThenSP1: true},
			{key: "BuyStraddles", label: "買進跨式",		BS1: 'B', BS2: 'B', CP1: 'C', CP2: 'P', SP1: 'S', SP2: 'S', colors:[1, 0, 1], lockMTH: true, lockSP: true},
			{key: "SellStraddles", label: "賣出跨式",		BS1: 'S', BS2: 'S', CP1: 'C', CP2: 'P', SP1: 'S', SP2: 'S', colors:[0, 1, 0], lockMTH: true, lockSP: true},
			{key: "BuyStrangles", label: "買進勒式",		BS1: 'B', BS2: 'B', CP1: 'C', CP2: 'P', SP1: 'H', SP2: 'L', colors:[1, 0, 1], lockMTH: true, sp2LessThenSP1: true},
			{key: "SellStrangles", label: "賣出勒式",		BS1: 'S', BS2: 'S', CP1: 'C', CP2: 'P', SP1: 'H', SP2: 'L', colors:[0, 1, 0], lockMTH: true, sp2LessThenSP1: true},
			{key: "Conversions", label: "轉換",				BS1: 'B', BS2: 'S', CP1: 'P', CP2: 'C', SP1: 'S', SP2: 'S', colors:[1, 0], lockMTH: true, lockSP: true},
			{key: "Reversals", label: "逆轉",				BS1: 'S', BS2: 'B', CP1: 'P', CP2: 'C', SP1: 'S', SP2: 'S', colors:[0, 1], lockMTH: true, lockSP: true},
		];
		return {
			selectedStrategy: {},
			strategyList: list,
			defaultStrategyList: JSON.parse(JSON.stringify(list)),
		}
	},
	beforeMount() {
		this.$store.state.opt.defaultStrategyList = this.defaultStrategyList;
		// 預設顯示第一個策略
		if (!vuex.plOrder.selectedStrategy.key)
			this.selectedStrategy = this.$store.state.plOrder.selectedStrategy = this.strategyList[0];
		// 有帶入預設要顯示的策略時
		if (this.defaultStrategyName) {
			this.selectedStrategy = this.strategyList.find((obj)=>{return obj.key===this.defaultStrategyName});
		}
		// 複製此策略設定物件送出
		this.$emit("change", JSON.parse(JSON.stringify(this.selectedStrategy)));
	},
    mounted() {},
	beforeDestroy() {},
	components: {},
    methods: {
		onBtnClose() {
			this.$emit("close");
			this.$refs.ctn.style.top = `100%`;
		},
		onBtnStrategy(obj) {
			if (obj === this.selectedStrategy) {
				this.onBtnClose();
			}
			else {
				this.selectedStrategy = this.$store.state.plOrder.selectedStrategy = obj;
				this.$emit("change", JSON.parse(JSON.stringify(this.selectedStrategy)));
			}
		},
		onBtnHelp() {
			eventBus.$emit("POPUP-DIALOG", PlotOrderStrategyHelpTW, '', {transName: 'float'});
		}
	},
	watch: {},
    computed: {},
}
</script>

