<template>
    <div class="ivrank-ctn flex-col">
		<!-- 表頭列 -->
		<MyHead v-if="isPortrait" class="head" :backType="1" :showText="$ln('IV 龙虎榜')"></MyHead>
		<!-- 选择权下拉选单 -->
    	<div class="flex-row pd1 list-block">
			<SingleSelect class="ssctn1" v-touching :options="opt1List" v-model="optExg" />
			<SingleSelect class="ssctn2" v-touching :options="opt2List" v-model="optSym" />
			<SingleSelect class="ssctn3" v-touching :options="opt3List" v-model="optMon" />
		</div>
		<div class="body flex-1 posr">
			<div class="FULL scrolling-y pdlr1" v-stop-propagation-y>
				<div class="list-block" v-if="isPortrait">
					<div class="iv-block flex-col" v-for="obj in filteredIVRankList"
						:class="{high: obj.ivlv>=70, mid: obj.ivlv>=41 && obj.ivlv<=70, low: obj.ivlv<=40}">
						<div class="flex-center pd1 font-size-xl nowrap font-bold">{{getName(obj.s)}}</div>
						<div class="flex-center pd1 clr-black">{{$ln(`IV 等级`)}}</div>
						<div class="flex-center pd1 flex-1 iv-ctn clr-black">{{obj.ivlv}}</div>
					</div>
				</div>
			</div>
		</div>
    </div>
</template>

<script>
import OptionSelector from "@/components/OptionSelector.vue";
export default {
	props: [],
	data() {
		return {
			/** 下拉選單決定當前的 contracts */
			contracts: {},

			ivRankList: M4C.OptCloud.queryIVRank(),
			exgMap: {},
			sidMap: {},
			monMap: {},
			optExg: '',
			optSym: '',
			optMon: '',
		}
	},
	beforeMount() {
	},
    mounted() {},
	beforeDestroy() {},
	components: {
		OptionSelector,
	},
    methods: {
		getName(sid) {
			return M4C.Symbol.getCNM4(sid);
		}
	},
	watch: {
		ivRankList(nv) {
			nv.forEach((obj)=>{
				let exg = obj.$MINFO.Exchange;
				let sid = obj.$MINFO.Symbol;
				let mon = obj.s.split('.')[4];
				this.$set(this.exgMap, exg, exg);
				this.$set(this.sidMap, sid, obj.$MINFO.Name[this.langKey]);
				this.$set(this.monMap, mon, mon);
			});
		}
	},
    computed: {
		// 響應式轉向變數 (直向=true)
		isPortrait() {
			return this.$store.state.status.isPortrait;
		},

		opt1List() {
			let list = [{label: "全部交易所", value: "*"}];
			for (let key in this.exgMap) {
				let exgObj = M4C.Symbol.mapExgIDtoExgObj[`X4_OPT.${key}`] || M4C.Symbol.mapExgIDtoExgObj[key];
				let val = exgObj[this.langKey];
				list.push({label: val, value: key});
			}
			return list;
		},
		opt2List() {
			let list = [{label: "全部商品", value: "*"}];
			for (let key in this.sidMap) {
				list.push({label: this.sidMap[key], value: key});
			}
			return list;
		},
		opt3List() {
			let list = [{label: "全部月份", value: "*"}];
			for (let key in this.monMap) {
				list.push({label: key, value: key});
			}
			return list;
		},
		/** 經過選單過濾的 IVRank */
		filteredIVRankList() {
			return this.ivRankList.filter((obj)=>{
				let tmp = obj.s.split('.');
				if (this.optExg !== "*" && this.optExg !== tmp[2]) return false;
				if (this.optSym !== "*" && this.optSym !== tmp[3]) return false;
				if (this.optMon !== "*" && this.optMon !== tmp[4]) return false;
				return true;
			});
			return ivRankList;
		},
		/** 系統語系 */
		langKey() {
			return this.$store.state.lang.mainformLangKey;
		}
	},
}
</script>

<style scoped>
.ssctn1 {width: 35vw;}
.ssctn2 {width: 31vw;}
.ssctn3 {width: 31vw;}
.iv-block {
	width: 40vw;
	height: 40vw;
	margin: 4vw;
	border-radius: 5vw;
	background-size: cover;
}
.iv-block.high {
	background-image: url('data:image/png;base64,');
}
.iv-block.mid {
	background-image: url('data:image/png;base64,');
}
.iv-block.low {
	background-image: url('data:image/png;base64,');
}
.iv-ctn {
	font-size: 42px;
}
</style>